import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [completedDays, setCompletedDays] = useState(new Set());
  const [fireworksVisible, setFireworksVisible] = useState(false);

  const handleClick = (day) => {
    const newCompletedDays = new Set(completedDays);
    if (newCompletedDays.has(day)) {
      newCompletedDays.delete(day); // Unmark if already clicked
    } else {
      newCompletedDays.add(day); // Mark as completed
    }
    setCompletedDays(newCompletedDays);
  };

  useEffect(() => {
    // Show fireworks if all 7 days are completed
    if (completedDays.size === 7) {
      setFireworksVisible(true);
      setTimeout(() => setFireworksVisible(false), 3000); // Hide fireworks after 3 seconds
    }
  }, [completedDays]);

  const isAllDaysCompleted = completedDays.size === 7;

  return (
    <header>
      <div className="container">
        <Link to="/">
          <h1>Workout Buddy</h1>
        </Link>
        <div className="week-days">
          {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => (
            <div
              key={day}
              className={`day-circle ${completedDays.has(day) ? 'completed' : ''}`}
              onClick={() => handleClick(day)}
            >
              {day}
            </div>
          ))}
        </div>
      </div>
      {fireworksVisible && <div className="fireworks-animation">ðŸŽ†</div>}
    </header>
  );
};

export default Navbar;
