import { useState } from 'react';
import { useWorkoutsContext } from '../hooks/useWorkoutsContext';

// date fns
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext();
  const [isEditing, setIsEditing] = useState(false);
  const [editedWorkout, setEditedWorkout] = useState({
    title: workout.title,
    load: workout.load,
    reps: workout.reps,
  });

  const handleClick = async () => {
    const response = await fetch('/api/workouts/' + workout._id, {
      method: 'DELETE',
    });
    const json = await response.json();

    if (response.ok) {
      dispatch({ type: 'DELETE_WORKOUT', payload: json });
    }
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedWorkout({ ...editedWorkout, [name]: value });
  };

  const handleSave = async () => {
    const response = await fetch('/api/workouts/' + workout._id, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(editedWorkout),
    });
    const json = await response.json();

    if (response.ok) {
      dispatch({ type: 'UPDATE_WORKOUT', payload: json });
      setIsEditing(false);
    }
  };

  return (
    <div className="workout-details">
      {isEditing ? (
        <>
          <input
            type="text"
            name="title"
            value={editedWorkout.title}
            onChange={handleChange}
            placeholder="Title"
          />
          <input
            type="number"
            name="load"
            value={editedWorkout.load}
            onChange={handleChange}
            placeholder="Load (kg)"
          />
          <input
            type="number"
            name="reps"
            value={editedWorkout.reps}
            onChange={handleChange}
            placeholder="Reps"
          />
          <button onClick={handleSave} className="save-button">
            Save
          </button>
          <button onClick={handleEditToggle} className="cancel-button">
            Cancel
          </button>
        </>
      ) : (
        <>
          <h4>{workout.title}</h4>
          <p>
            <strong>Load (kg): </strong>
            {workout.load}
          </p>
          <p>
            <strong>Number of reps: </strong>
            {workout.reps}
          </p>
          <p>
            {formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}
          </p>
          <span className="material-symbols-outlined" onClick={handleClick}>
            delete
          </span>
          <span className="material-symbols-outlined edit-button" onClick={handleEditToggle}>
            edit
          </span>
        </>
      )}
    </div>
  );
};

export default WorkoutDetails;
