import { useState } from 'react';
import { useWorkoutsContext } from '../hooks/useWorkoutsContext';
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext();
  const [isEditing, setIsEditing] = useState(false);
  const [editedWorkout, setEditedWorkout] = useState({
    title: workout.title,
    load: workout.load,
    reps: workout.reps,
  });

  // Handle edit toggle
  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedWorkout((prev) => ({ ...prev, [name]: value }));
  };

  // Handle save (PATCH request)
  const handleSave = async () => {
    try {
      const response = await fetch(`/api/workouts/${workout._id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editedWorkout),
      });

      if (response.ok) {
        const updatedWorkout = await response.json();

        // Dispatch update action to context
        dispatch({ type: 'UPDATE_WORKOUT', payload: updatedWorkout });

        // Exit edit mode
        setIsEditing(false);
      } else {
        console.error('Failed to update workout');
      }
    } catch (error) {
      console.error('Error updating workout:', error);
    }
  };

  // Handle delete
  const handleDelete = async () => {
    try {
      const response = await fetch(`/api/workouts/${workout._id}`, {
        method: 'DELETE',
      });
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'DELETE_WORKOUT', payload: json });
      }
    } catch (error) {
      console.error('Error deleting workout:', error);
    }
  };

  return (
    <div className="workout-details">
      {isEditing ? (
        <>
          <input
            type="text"
            name="title"
            value={editedWorkout.title}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="load"
            value={editedWorkout.load}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="reps"
            value={editedWorkout.reps}
            onChange={handleInputChange}
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={handleEditToggle}>Cancel</button>
        </>
      ) : (
        <>
          <h4>{workout.title}</h4>
          <p>
            <strong>Load (kg): </strong>
            {workout.load}
          </p>
          <p>
            <strong>Reps: </strong>
            {workout.reps}
          </p>
          <p>
            {formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}
          </p>
          <span onClick={handleDelete}>Delete</span>
          <span onClick={handleEditToggle}>Edit</span>
        </>
      )}
    </div>
  );
};

export default WorkoutDetails;
