import { Link } from 'react-router-dom';
import { useState } from 'react';

// Define the different moods based on the number of completed days
const getMood = (completedDaysCount) => {
  if (completedDaysCount === 7) return 'ðŸ˜'; // All days completed
  if (completedDaysCount >= 4) return 'ðŸ˜Š'; // More than half completed
  if (completedDaysCount > 0) return 'ðŸ˜'; // Some days completed
  return 'ðŸ˜ž'; // No days completed
};

const Navbar = () => {
  // State to track which days are marked as done
  const [completedDays, setCompletedDays] = useState([]);

  // Function to handle day click (toggle completion)
  const handleDayClick = (day) => {
    setCompletedDays((prev) =>
      prev.includes(day) ? prev.filter((d) => d !== day) : [...prev, day]
    );
  };

  // Get the mood based on the completed days
  const mood = getMood(completedDays.length);

  return (
    <header>
      <div className="container">
        <Link to="/">
          <h1>Workout Buddy</h1>
        </Link>

        <div className="week-days">
          {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((day, index) => (
            <div
              key={index}
              className={`day-circle ${completedDays.includes(day) ? 'completed' : ''}`}
              onClick={() => handleDayClick(day)}
            >
              {day}
            </div>
          ))}
        </div>

        {/* Mood face on the right side */}
        <div className="mood-container">
          <span className="mood-face">{mood}</span>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
