import { useState } from 'react';
import { useWorkoutsContext } from '../hooks/useWorkoutsContext';
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext();
  const [isEditing, setIsEditing] = useState(false);
  const [editedWorkout, setEditedWorkout] = useState({
    title: workout.title,
    load: workout.load,
    reps: workout.reps,
  });

  const handleDelete = async () => {
    const response = await fetch(`/api/workouts/${workout._id}`, {
      method: 'DELETE',
    });
    const json = await response.json();

    if (response.ok) {
      dispatch({ type: 'DELETE_WORKOUT', payload: json });
    }
  };

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedWorkout({ ...editedWorkout, [name]: value });
  };

  const handleSave = async () => {
    const response = await fetch(`/api/workouts/${workout._id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editedWorkout),
    });

    if (response.ok) {
      const updatedWorkout = await response.json();
      dispatch({ type: 'UPDATE_WORKOUT', payload: updatedWorkout });
      setIsEditing(false);
    } else {
      console.error('Failed to update workout.');
    }
  };

  return (
    <div className="workout-details">
      {isEditing ? (
        <div>
          <input
            type="text"
            name="title"
            value={editedWorkout.title}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="load"
            value={editedWorkout.load}
            onChange={handleInputChange}
          />
          <input
            type="number"
            name="reps"
            value={editedWorkout.reps}
            onChange={handleInputChange}
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={handleEditToggle}>Cancel</button>
        </div>
      ) : (
        <>
          <h4>{workout.title}</h4>
          <p>
            <strong>Load (kg): </strong>
            {workout.load}
          </p>
          <p>
            <strong>Reps: </strong>
            {workout.reps}
          </p>
          <p>{formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}</p>
          <span onClick={handleDelete}>Delete</span>
          <span onClick={handleEditToggle}>Edit</span>
        </>
      )}
    </div>
  );
};

export default WorkoutDetails;
