import { useEffect, useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";

const WorkoutTracker = () => {
  const { workouts } = useWorkoutsContext();
  const [stats, setStats] = useState({
    totalWorkouts: 0,
    avgLoad: 0,
    mostCommonCategory: "",
  });

  useEffect(() => {
    if (workouts && workouts.length > 0) {
      const totalWorkouts = workouts.length;
      const totalLoad = workouts.reduce((sum, workout) => sum + workout.load, 0);
      const avgLoad = totalLoad / totalWorkouts;

      const categoryCount = {};
      workouts.forEach((workout) => {
        if (workout.category) {
          categoryCount[workout.category] =
            (categoryCount[workout.category] || 0) + 1;
        }
      });

      const mostCommonCategory = Object.keys(categoryCount).reduce(
        (a, b) => (categoryCount[a] > categoryCount[b] ? a : b),
        ""
      );

      setStats({ totalWorkouts, avgLoad: avgLoad.toFixed(2), mostCommonCategory });
    }
  }, [workouts]);

  return (
    <div className="workout-tracker">
      <h3>Workout Tracker</h3>
      <p>Total Workouts: {stats.totalWorkouts}</p>
      <p>Average Load: {stats.avgLoad} kg</p>
      <p>Most Common Category: {stats.mostCommonCategory || "N/A"}</p>
    </div>
  );
};

export default WorkoutTracker;
