import PropTypes from 'prop-types'; // For prop validation
import { useWorkoutsContext } from '../hooks/useWorkoutsContext';
import formatDistanceToNow from 'date-fns/formatDistanceToNow';

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext();
  const [isLoading, setIsLoading] = useState(false);

  const handleAction = async (action) => {
    setIsLoading(true);
    const method = action === 'delete' ? 'DELETE' : 'PATCH';
    const endpoint = `/api/workouts/${workout._id}`;

    try {
      const response = await fetch(endpoint, { method });
      const json = await response.json();

      if (!response.ok) {
        console.error(json.error || 'Something went wrong');
        setIsLoading(false);
        return;
      }

      const actionType = action === 'delete' ? 'DELETE_WORKOUT' : 'EDIT_WORKOUT';
      dispatch({ type: actionType, payload: json });
    } catch (err) {
      console.error('Request failed:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="workout-details">
      <h4>{workout.title}</h4>
      <p>
        <strong>Load (kg): </strong>
        {workout.load}
      </p>
      <p>
        <strong>Number of reps: </strong>
        {workout.reps}
      </p>
      <p>
        {formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}
      </p>
      <button
        className="material-symbols-outlined delete-button"
        onClick={() => handleAction('delete')}
        disabled={isLoading}
        aria-label="Delete Workout"
      >
        delete
      </button>
      <button
        className="material-symbols-outlined edit-button"
        onClick={() => handleAction('edit')}
        disabled={isLoading}
        aria-label="Edit Workout"
      >
        edit
      </button>
    </div>
  );
};

WorkoutDetails.propTypes = {
  workout: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    load: PropTypes.number.isRequired,
    reps: PropTypes.number.isRequired,
    createdAt: PropTypes.string.isRequired,
  }).isRequired,
};

export default WorkoutDetails;
